using System.Security.Policy;
using System.Text.RegularExpressions;
using System.Windows.Input;

namespace BF1ServerTools.RES.Data;

public class TranslateKeyData {
    public enum TranslateKeyFlag {
        NOTHING,
        TOO_LONG,
        OFFENSIVE,
        WHITESPACE,
        MULTILINE,
        FAKE_ENGLISH
    }

    public class TranslateKey {
        public string Hash;
        public string DisplayText;
        public TranslateKeyFlag Flag;

        public TranslateKey(string hash, string displayText) {
            this.Hash = hash;
            this.DisplayText = displayText;
            if (displayText.Trim().Length == 0) {
                this.Flag = TranslateKeyFlag.WHITESPACE;
            } else if (displayText.Contains("\n")) {
                this.Flag = TranslateKeyFlag.MULTILINE;
            } else if (displayText.Length > 20) {
                this.Flag = TranslateKeyFlag.TOO_LONG;
            } else if (Regex.IsMatch(displayText, @"^[A-Za-z0-9\-\_ ]+$")) {
                this.Flag = TranslateKeyFlag.FAKE_ENGLISH;
            } else {
                this.Flag = TranslateKeyFlag.NOTHING;
            }
        }

        public TranslateKey(string hash, string displayText, TranslateKeyFlag flag) {
            this.Hash = hash;
            this.DisplayText = displayText;
            this.Flag = flag;
        }

        public string ToRule() {
            return Hash + " " + DisplayText;
        }
    }

